@bot.message_handler(commands=['start'])
def start(message):
    markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
    btn1 = types.KeyboardButton("üê± –ö–æ—Ç")
    btn2 = types.KeyboardButton("üêï –°–æ–±–∞–∫–∞")
    btn3 = types.KeyboardButton("üé≤ –†–∞–Ω–¥–æ–º–∞–π–∑–µ—Ä")
