    btn5 = types.KeyboardButton("‚ùì –û–ø—Ä–æ—Å")

    markup.add(btn1, btn2, btn3, btn4, btn5)

    bot.send_message(message.chat.id, text="–ü—Ä–∏–≤–µ—Ç. –Ø –±–æ—Ç –¥–ª—è –ø–æ–¥–Ω—è—Ç–∏—è –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏—è".format(message.from_user), reply_markup=markup)


