def car_name(message):

            command = message.text.split(" ")
            command_len = len(command)
            try:
                if command_len == 1:
                    bot.reply_to(message, random.randint(1,100))
                elif command_len == 2:
                    bot.reply_to(message, random.randint(1, int(command[1])))
                elif command_len == 3:
                    bot.reply_to(message, random.randint(int(command[1]), int(command[2])))
                else:
                    bot.reply_to(message, "–ú–∞–∫—Å–∏–º–∞–ª—å–Ω–∞ –∫—ñ–ª—å–∫—ñ—Å—Ç—å –ø–∞—Ä–∞–º–µ—Ç—Ä—ñ–≤ - 2.")
            except Exception:
                bot.reply_to(message, "–©–æ—Å—å –ø—ñ—à–ª–æ –Ω–µ —Ç–∞–∫, —Å–ø—Ä–æ–±—É–π—Ç–µ –∑–Ω–æ–≤—É.")






